import React from 'react';

import { withRouter, useRouter } from 'next/router';

import { PRODUCT_DATA } from '../data/data-store';

function ProductSingle({ router }) {
  const [currProduct, setCurrProduct] = useState(PRODUCT_DATA)

  

  let curr = PRODUCT_DATA.find(
    (e) => e.productName === router.query.productid
  );

  return (
    <div className='single-product'>
      <div className='single-product__photos'>
        <img
          className='single-product__photo'
          src={curr.productphotos[0]}
          alt={curr.productName}
        />
      </div>
      <div className='single-product__details'>
        <h2 className='single-product__title'>{curr.productName}</h2>
      </div>
    </div>
  );
}

export default withRouter(ProductSingle);
