     const current = window.location.pathname.split('/')[1];
     console.log(current);
  })

  const pages = [
    {}
  ]


  return (
    <div className="side-nav">
      <Link href='/shop'>
        <a className='side-nav__item' >SHOP</a>
      </Link>
      <Link href='/collections'>
        <a className='side-nav__item' >COLLECTIONS</a>
      </Link>
      <Link href='/shopInsta'>
        <a className='side-nav__item' >SHOP INSTA</a>
      </Link>
      <Link href='/blog'>
        <a className='side-nav__item' >BLOG</a>
      </Link>
      <Link href='/contact'>
        <a className='side-nav__item' >CONTACT US</a>
      </Link>
      <Link href='/'>
        <a className='side-nav__item' >HOME</a>
      </Link>
    </div>
  );
};

export default SideNav;
