import React from 'react';
import Link from 'next/link';

import moduleName from '../utils';

function ListItem(props) {
  // let name = props.name;

  // if (props.name.includes('-')) {
  //   console.log('includes caught an item:', props.name);

  //   name = props.name.replace('-',' ');
  //   console.log('the new name is:',name);
  //   // return name;
  // }

  return (
    <div className='list-item'>
      <img
        src={props.photos[1]}
        alt={props.name}
        className='list-item__bg-img'
      />
      <img src={props.photos[0]} alt={props.name} className='product-photo' />
      <div className='item-details'>
        <Link href={`/product?productid=${props.name}`} as={`/shop/${props.name}`}> 
          <h2 className='product-name'>{name}</h2>
        </Link>
        <span className='price'>{props.price}</span>
      </div>
    </div>
  );
}

export default ListItem;
