import React from 'react';

import { useRouter } from 'next/router';

import { PRODUCT_DATA } from '../data/data-store';

function ProductSingle(props) {
  const router = useRouter();
  
  let currProduct = PRODUCT_DATA.find(e => e.productName === router.query.productid);

  console.log('our current product is:', currProduct);

  return (
    <div className='single-product'>
      <div className='single-product__details'>
        <h2 className="single-product__title">{currProduct.productName}</h2>
      </div>
      <img
        className='single-product__photo'
        src={currProduct.productphotos[0]}
        alt={currProduct.productName}
      />
    </div>
  );
}

export default ProductSingle;
