import App, { Container } from 'next/app'
import SideNav from '../components/side-nav.component';

const ShadzApp = ({ Component, pageProps }) => {
  
  const styles = {
    display: 'flex',
    padding: '0 !important',
  };

  return (
    <Container style>
      <SideNav />
      <Component {...pageProps} />
    </Container>
  );
}

export default ShadzApp;
