import SideNav from '../components/side-nav.component';

const ShadzApp = ({ Component, pageProps }) => {
  
  const styles = {
    display: 'flex',
    padding: '0 !important',
  };

  return (
    <div style={styles}>
      <ul>TOP NAV</ul>
      <div className='center-container'>
        <SideNav />
        <Component {...pageProps} />
      </div>
      <footer className="footer">FOOTER</footer>
    </div>
  );
}

export default ShadzApp;
